={{ 
$json.messages.map(message => {
  let role

  switch(message.id[2]) {
    case "SystemMessage":
      role = "system"
      break;
    case "HumanMessage":
      role = "user"
      break;
    case "AIMessage":
      role = "assistant"
      break;
  }

  return {role: role, content: message.kwargs.content}
})
}}

