// Get the value of the "output" field
const output = $input.first().json.output

// Get the output with type "output_text"
const output_message = output.find(output =>
  output.type === "message"
)

// Extract evaluation result
let draft = output_message.content[0].text

return {
  "success": true,
  "executionId": "0",
  "data": {
    "title": "",
    "content": draft,
    "citations": [],
    "outline": []
  }
}
